mapper.insert:
INSERT INTO
	board
(boardnum, userid, title, content)
VALUES (
	board_seq.nextval
	,#{userid}
	,#{title}
	,#{content}
)

mapper.update:
UPDATE
	board
SET
	title = #{title}
	,content = #{content}
WHERE
	boardnum = #{boardNum}
	AND userid = #{userid}

mapper.delete:
DELETE
	board
WHERE
	boardnum = #{boardNum}
	AND userid = #{userid}
